import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Client } from 'src/app/models/Client';
import { ClientService } from 'src/app/service/client.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit {

  clientForm: Client = {
    id: '',
    name: '',
    phone: 0,
    address: '',
    email: '',
  };
  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private clientService: ClientService,
  ) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe((param) => {
      var id = String(param.get('id'));
      this.getById(id);
    });
  }

  getById(id: string) {
    this.clientService.getById(id).subscribe((data) => {
      this.clientForm = data;
    });
  }

  update() {
    this.clientService.update(this.clientForm)
      .subscribe({
        next: (data) => {
          this.router.navigate(["/client/home"]);
        },
        error: (err) => {
          console.log(err);
        }
      })
  }
}



<div class="container">
    <legend>Edit Item</legend>
    <form>
      <div class="mb-3">
        <label for="txtName" class="form-label">Name</label>
        <input type="text" name="name" [(ngModel)] = "clientForm.name" class="form-control" id="txtName" />
      </div>
      <div class="mb-3">
        <label for="txtPhone" class="form-label">Phone</label>
        <input type="number" name="phone" [(ngModel)] = "clientForm.phone" class="form-control" id="txtPhone" />
      </div>
      <div class="mb-3">
        <label for="txtAddress" class="form-label">Address</label>
        <input type="text" name="address" [(ngModel)] = "clientForm.address" class="form-control" id="txtAddress" />
      </div>
      <div class="mb-3">
        <label for="txtEmail" class="form-label">Email</label>
        <input type="text" name="email" [(ngModel)] = "clientForm.email" class="form-control" id="txtEmail" />
      </div>

      <div>
        <button class="button" type="button" (click)="update()" class="btn btn-primary mx-1">Update</button>
        <button class="button" type="button"  routerLink="/client/home" class="btn btn-danger mx-1" >Back</button>
      </div>
    
    </form>
</div>
