import { Component, OnInit } from '@angular/core';
import { Client, ClientDto } from 'src/app/models/Client';
import { ClientService } from 'src/app/service/client.service';
import { Pipe, PipeTransform } from '@angular/core';

@Pipe({ name: 'listFilter'})
export class ListFilterPipe implements PipeTransform {

    transform(list: any[], filterText: string): any {
        return list ? list.filter(Client => Client.name.search(new RegExp(filterText, 'i')) > -1) : [];
    }
}

declare var window: any;
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  clients: Client[] = [];
  clientsDto: ClientDto[] = [];

  deleteModal: any;
  idTodelete: string = '';
  searchTerm: string ='';
  page = 1;
  pageSize = 4;
  collectionSize: number= 0;
  currentRate = 8;


  constructor(private clientService: ClientService) { }

  ngOnInit(): void {

    this.deleteModal = new window.bootstrap.Modal(
      document.getElementById('deleteModal')
    );


    
    this.clientService.get().subscribe((data: ClientDto[]) => {
      this.clients = data;
      this.clientsDto = this.inintProductDto(this.clients);
      console.log(this.clients);
    });


    this.get();


  }

  search(value: string): void {
    this.clients = this.clients.filter((val) => val.name.toLowerCase().includes(value));
    this.collectionSize = this.clients.length;
  }



  get() {
    this.clientService.get().subscribe((data) => {
      this.clients = data;

    });
  }


  inintProductDto(clients: Client[]): ClientDto[] {
    
    let tempClientDto: ClientDto[] = [];

    clients.forEach((client) => {

      const restDto: ClientDto = {
        id: client.id,
        name: client.name,
        phone: client.phone,
        address: client.address,
        email: client.email,
       
      };

      tempClientDto.push(restDto);

    });

    return tempClientDto;
  }

  openDeleteModal(id: string) {
    this.idTodelete = id;
    this.deleteModal.show();
  }


/************JN:FIXING DELETE PROBLEM*********************/
  delete() {
    this.clientService.delete(this.idTodelete).subscribe({
      next: (data: any) => {
        this.clients = this.clients.filter(_ =>_.id !=  this.idTodelete);
        this.clientsDto = this.inintProductDto(this.clients);
        this.deleteModal.hide();
      }
    });
  }

}
