import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from 'src/app/models/Product';
import { DataUtils,FileLoadError} from 'src/app/service/data-util.service';
import { EventManager } from 'src/app/service/event-manager.service';
import { ProductService } from 'src/app/service/product.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})

export class EditComponent implements OnInit {

  productForm: FormGroup;

 editproduct: Product = {

    id: '',
    name: '',
    price: 0,
    photo: null,
    photoContentType: '',
    description: '',
    tax: 0,

  };




  constructor(
    protected dataUtils: DataUtils,
    protected eventManager: EventManager,
    private fb: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductService,
  ) { 



  }


  ngOnInit(): void {
    this.route.paramMap.subscribe((param) => {
      var id = String(param.get('id'));
      this.getById(id);
    });
  }

  getById(id: string) {
    this.productService.getById(id).subscribe((data) => {
      this.productForm = data;
    });
  }

  update() {
    this.productService.update(this.productForm)
      .subscribe({
        next: (data) => {
          this.router.navigate(["/product/home"]);
        },
        error: (err) => {
          console.log(err);
        }
      })
  }

//--------------JN ADD MAIN PHOTO STARTS--------//



onEdit(product: Product) {


}



byteSize(base64String: string): string {
  return this.dataUtils.byteSize(base64String);
}

openFile(base64String: string, contentType: string | null | undefined): void {
  this.dataUtils.openFile(base64String, contentType);
}



setFileData(event: Event, field: string, isImage: boolean): void {
  this.dataUtils.loadFileToForm(event, ,field, isImage).subscribe({
    error: (err: FileLoadError) =>
      this.eventManager.broadcast('big error'),
  });
}

//--------------JN ADD MAIN PHOTO ENDS--------//




}




  <div class="container">
    <legend>Edit Product</legend>
    <form>
      <div class="mb-3">
        <label for="txtName" class="form-label">Name</label>
        <input type="text" name="name" [(ngModel)] = "productForm.name" class="form-control" id="txtName" />
      </div>
      <div class="mb-3">
        <label for="txtPrice" class="form-label">Price</label>
        <input type="number" name="price" [(ngModel)] = "productForm.price" class="form-control" id="txtPrice" />
      </div>


        <!-------------JN Adding main image start----------->

    <div class="row mb-3">
      <label class="form-label" for="field_photo"> Photo</label>

      <input type="hidden" class="form-control" name="photo" id="field_photo" data-cy="photo" formControlName="photo" />
      <input type="hidden" class="form-control" name="photoContentType" id="field_photoContentType"
        formControlName="photoContentType" />
    </div>

    <!-------------JN Adding main image start----------->








      <div class="mb-3">
        <label for="txtDescription" class="form-label">Description</label>
        <input type="text" name="description" [(ngModel)] = "productForm.description" class="form-control" id="txtDescription" />
      </div>
      <div class="mb-3">
        <label for="txtTax" class="form-label">tax</label>
        <input type="text" name="tax" [(ngModel)] = "productForm.tax" class="form-control" id="txtTax" />
      </div>

      <div>
        <button class="button" type="button" (click)="update()" class="btn btn-primary mx-1">Update</button>
        <button class="button" type="button"  routerLink="/client/home" class="btn btn-danger mx-1" >Back</button>
      </div>

    </form>
  </div>
