import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { environment } from 'src/environments/environment';
import { Product, ProductDto } from '../models/Product';
import { HttpClient } from '@angular/common/http';


@Injectable({
  providedIn: 'root'
})
export class ProductService {
  private url = environment.urlProducts;
  private urlCreate = environment.urlProductsCreate;

  constructor(private http: HttpClient) { }


    getProducts(): Observable<ProductDto[]> {
      return this.http.get<ProductDto[]>(`${this.url}`);
    }
    create(payload: ProductDto) {
      return this.http.post<ProductDto>(`${this.urlCreate}`, payload);
    }
    getById(id: number) {
      return this.http.get<Product>(`http://localhost:8080/api/products/${id}`);
     }
  }
