import { Component, ElementRef, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'admindashboard';

  constructor(private el:ElementRef){}
  ngOnInit(): void {

    let alldrpdwn = document.querySelectorAll('.dropdow-container');
    console.log(alldrpdwn,'alldrpdwn#');
    alldrpdwn.forEach((item:any)=>{
      const a = item.parentElement?.querySelector('a:first-child');
      console.log(a,'a#');
      a.addEventListener('click',(e:any)=>{
          e.preventDefault();
          this.el.nativeElement.classList.toggle('active');
          item.classList.toggle('show');
      });
      
    });

  }


  // responsivemenu 
  responsiveMenu:any;
  // responsivemaincontent
  responsiveContent:any;
  defaultStatus=true;
  openNav(status:any)
  {
    if(status===this.defaultStatus)
    {
      this.responsiveMenu = {
        'display':'block'
      }
      this.responsiveContent={
        'margin-left':'150px'
      }
      this.defaultStatus = false;
    }else
    {
      this.responsiveMenu = {
        'display':null
      }
      this.responsiveContent={
        'margin-left':null
      }
      this.defaultStatus=true;
    }

  }


}
<!-- topnavbar -->
<div class="topnav shadow p-1">
    <a>Admin Dashboard</a>
    <span class="menu" (click)="openNav(true)"><i class="bi bi-list"></i></span>
</div>
<!-- topnavbar -->

<!-- left sidebar nav  -->

<div  class="sidebar shadow p-2"  id="sidebar"  [ngStyle]="responsiveMenu">
    <ul>
        <li><a routerLink=""><i class="bi bi-speedometer2"></i> Dashboard</a></li>
        <li><a routerLink="/client"><i class="bi bi-person-gear"></i> Client</a>
            <ul class="dropdow-container">
                <li><a routerLink="/create">Create</a></li>
                <li><a routerLink="/client/update">Update</a></li>
            </ul>
        </li>
        <li><a routerLink="/invoice"><i class="bi bi-card-text"></i> Invoice</a></li>
        <li>
            <a routerLink="/product/home"><i class="bi bi-bag"></i> Products <!-- <i class="bi bi-arrow-right icon"></i> --></a>
            <ul class="dropdow-container">
                <li><a href="#">Create</a></li>
                <li><a href="#">Update</a></li>
            </ul>
        </li>
        <li>
            <a routerLink="/sale"><i class="bi bi-tag"></i> Sale <!-- <i class="bi bi-arrow-right icon"></i> --></a>
   
        </li>
        <li><a routerLink="/users"><i class="bi bi-person-gear"></i> User</a></li>
        <li><a href="#"><i class="bi bi-box-arrow-left"></i> Logout</a></li>

    </ul>
</div>
<!-- left sidebar nav  -->


<!-- main content  -->

<div class="main-content" [ngStyle]="responsiveContent">
    <router-outlet></router-outlet>
</div>

<!-- main content  -->
