import { Component, OnInit } from '@angular/core';
import { Client, ClientDto } from 'src/app/models/Client';
import { ClientService } from 'src/app/service/client.service';

declare var window: any;
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  clients: Client[] = [];
  clientsDto: ClientDto[] = [];

  deleteModal: any;
  idTodelete: string = '';


  constructor(private clientService: ClientService) { }

  ngOnInit(): void {

    this.deleteModal = new window.bootstrap.Modal(
      document.getElementById('deleteModal')
    );



    this.clientService.get().subscribe((data: ClientDto[]) => {
      this.clients = data;
      this.clientsDto = this.inintProductDto(this.clients);
      console.log(this.clients);
    });


    this.get();


  }



  get() {
    this.productService.get().subscribe((data) => {
      this.products = data;

    });
  }


  inintProductDto(products: Product[]): ProductDto[] {
    let tempProductDto: ProductDto[] = [];

    products.forEach((product) => {

      const restDto: ProductDto = {
        id: product.id,
        name: product.name,

      };

      tempProductDto.push(restDto);

    });

    return tempProductDto;
  }




  openDeleteModal(id: string) {
    this.idTodelete = id;
    this.deleteModal.show();
  }


/************JN:FIXING DELETE PROBLEM*********************/
  delete() {
    this.productService.delete(this.idTodelete).subscribe({
      next: (data: any) => {
        this.products = this.products.filter(_ =>_.id !=  this.idTodelete);
        this.productsDto = this.inintProductDto(this.products);
        this.deleteModal.hide();
      }
    });
  }



}
